// src/components/ComplaintForm.js
import React, { useState } from 'react';
import api from '../../api';

const ComplaintForm = () => {
  const [formData, setFormData] = useState({
    utilisateurId: '',
    produitService: '',
    descriptionProbleme: '',
    categorie: '',
    email: '',
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await api.post('/reclamations', formData);
      alert('Réclamation soumise avec succès');
    } catch (error) {
      alert('Erreur lors de la soumission');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Soumettre une réclamation</h2>
      <input type="text" name="utilisateurId" placeholder="ID utilisateur" onChange={handleChange} required />
      <input type="text" name="produitService" placeholder="Produit ou Service" onChange={handleChange} required />
      <textarea name="descriptionProbleme" placeholder="Décrivez le problème" onChange={handleChange} required />
      <input type="text" name="categorie" placeholder="Catégorie (technique, service...)" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <button type="submit">Envoyer</button>
    </form>
  );
};

export default ComplaintForm;
