// src/components/ComplaintStatus.js
import React, { useState } from 'react';
import api from '../../services/api';

const ComplaintStatus = () => {
  const [id, setId] = useState('');
  const [reclamation, setReclamation] = useState(null);

  const fetchReclamation = async () => {
    try {
      const response = await api.get(`/reclamations/${id}`);
      setReclamation(response.data);
    } catch {
      alert('Réclamation introuvable');
    }
  };

  return (
    <div>
      <h2>Suivre une réclamation</h2>
      <input type="text" value={id} onChange={e => setId(e.target.value)} placeholder="ID de réclamation" />
      <button onClick={fetchReclamation}>Rechercher</button>
      {reclamation && (
        <div>
          <p><strong>Catégorie :</strong> {reclamation.categorie}</p>
          <p><strong>Description :</strong> {reclamation.descriptionProbleme}</p>
          <p><strong>Statut :</strong> {reclamation.statut}</p>
          <p><strong>Produit/Service :</strong> {reclamation.produitService}</p>
        </div>
      )}
    </div>
  );
};

export default ComplaintStatus;
